import './App.css';
import './components/component.css';
import Top from './components/header';
import Bottom from './components/footer';
import TopSlider from './components/topSlider';
import SupriceItem from './components/supriceContent';
import Gallery from './components/gallery';
import SpecialItem from './components/specialContent';
import Newsletter from './components/newsletter';
import TopButton from './components/topButton';
import {useState} from 'react';

function App() {
  const [mode, setMode] = useState('light');

  const toggleMode = () => {
    if (mode === 'light') {
      setMode('dark');
      document.body.style.backgroundColor = '#040d1a';
      document.body.style.color = 'white';
    }
    else {
      setMode('light');
      document.body.style.backgroundColor = 'white';
      document.body.style.color = '#040d1a';
    }
  }

  return (
    <div className="App"  mode={mode} toggleMode={toggleMode} >
      <Top mode={mode} toggleMode={toggleMode} />
      <TopSlider />
      <SupriceItem />
      <Gallery />
      <SpecialItem />
      <Newsletter />
      <Bottom />
      {/* <TopButton /> */}
    </div>
  );
}

export default App;
