import React from 'react';
import { useState, useEffect } from 'react';

export default function Top(props) {
    let forColor = props.mode === 'dark' ? '#fff' : '#000';
    let forBg = props.mode === 'dark' ? '#000' : '#fff';
    let shad = props.mode ==='dark'?'1px 1px 10px rgba(250, 250, 250, 0.411)':'1px 1px 10px rgba(0, 0, 0, 0.711)';
    // function onclick show user container
    const [myStyle, setMyStyle] = useState('user1');
    const userToggle = () => {
        if (myStyle === 'user1') {
            setMyStyle('user2')
        }
        else {
            setMyStyle('user1')
        }

    }
    /// paddinfg in navbar on scrolll
    const [navStyle, setNavStyle] = useState('px-2');
    useEffect(() => {
        window.addEventListener("scroll", () => {
            if (window.pageYOffset > 0) {
                setNavStyle('px-3');
            } else {
                setNavStyle('px-2');
            }
        });
    }, []);
    return (
        <nav className={`navbar navbar-expand-lg fixed-top py-2  ${navStyle}`} style={{ background: forBg,borderColor:forColor}}>
            <div className="container-fluid">
                <h2 className="logo">SARANG-Hey <img src="./images/sarang.png" /> </h2>
                <button className="navbar-toggler collapsed" type="button" data-bs-toggle="collapse"
                    data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span className="toggler-icon top-bar" style={{background:forColor}}></span>
                    <span className="toggler-icon middle-bar"></span>
                    <span className="toggler-icon bottom-bar"></span>
                </button>
                <div className="collapse navbar-collapse justify-content-end" id="navbarSupportedContent">
                    <ul className="navbar-nav ml-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <a className="nav-link active" aria-current="page" style={{color:forColor}}  href="#"><i className="fas fa-home"></i> Home</a>
                        </li>
                        <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle"  style={{color:forColor}}  href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown"
                                aria-expanded="false">
                                <i className="fas fa-search"></i> Explore
                            </a>
                            <ul className="dropdown-menu" aria-labelledby="navbarDropdown" style={{background:forBg}}>
                                <li><a className="dropdown-item" style={{color:forColor}}  href="#suprise">Suprise Events</a></li>
                                <li><a className="dropdown-item" style={{color:forColor}}  href="#special">Special Events</a></li>
                            </ul>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link"  style={{color:forColor}} href="#"><i className="far fa-address-book"></i> About Us</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link"  style={{color:forColor}}  href="#footer"><i className="fas fa-phone-alt"></i> Contact Us</a>
                        </li>
                    </ul>
                    {/* <form className="d-flex">
                        <a className="btn outcolor" href="./login1/login.html">Sign In</a>
                    </form> */}
                    <div className="user" >
                        <div className={`user-profile `} onClick={userToggle} style={{color:forColor}} >
                            <i className="fas fa-user" data-bs-toggle="tooltip" data-bs-placement="bottom"
                                title="User Profile"></i>
                        </div>
                        <div className={`user-container ${myStyle}`} style={{color:forColor, background:forBg,boxShadow:shad}}>
                            <div className="user-name">
                                <h5>Nishant Rajput</h5>
                            </div>
                            <ul className="">
                                <li className=""><i className='far fa-user-circle'></i><a style={{color:forColor}} href="">My Profile</a></li>
                                <li className=""><i className="fas fa-pencil-alt"></i><a style={{color:forColor}} href="">Edit Profile</a></li>
                                <li className=""><i className="far fa-envelope"></i><a style={{color:forColor}} href="" >Inbox</a></li>
                                <li className=""><i className="fas fa-cog"></i><a style={{color:forColor}} href="">Setting</a></li>
                                <li className=""><i className="far fa-question-circle"></i><a style={{color:forColor}} href=""> Help</a></li>
                                <li className=""><i className="fas fa-sign-out-alt"></i><a style={{color:forColor}} href="">Logout</a></li>
                            </ul>
                        </div>
                    </div>
                    <i class={`bx mybx bxs-${props.mode === 'white'?'moon':'sun'} bx-${props.mode === 'white'?'':'spin'}`} 
                    onClick={props.toggleMode} style={{color:forColor}}></i>
                </div>
            </div>
        </nav>
    );
}
