import './App.css';
import './components/component.css';
import Top from './components/header';
import Bottom from './components/footer';
import TopSlider from './components/topSlider';
import SupriceItem from './components/supriceContent';
import Gallery from './components/gallery';
import SpecialItem from './components/specialContent';
import Newsletter from './components/newsletter';
import TopButton from './components/topButton';
import { useState } from 'react';
import './components/component.css';
import Userprofile from './components/Userprofile';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";


function App() {
  const [mode, setMode] = useState('dark');
  const toggleMode = () => {
    if (mode === 'dark') {
      setMode('white');
      document.body.style.backgroundColor = '#fff';
    }
    else {
      setMode('dark');
      document.body.style.backgroundColor = '#000';
    }
  }
  let forColor = mode === 'dark' ? '#fff' : '#000';
  let forBg = mode === 'dark' ? '#000' : '#fff';
  return (
    <Router>
      <div className="App" mode={mode} toggleMode={toggleMode} style={{ background: forBg }} >
        <Top mode={mode} toggleMode={toggleMode} />
        <div>
          <Routes>
            <Route exact path="/userprofile">
              <Userprofile />
            </Route>
            <Route exact path="/">
              <TopSlider mode={mode} toggleMode={toggleMode} />
              <SupriceItem mode={mode} toggleMode={toggleMode} />
              <Gallery mode={mode} toggleMode={toggleMode} />
              <SpecialItem mode={mode} toggleMode={toggleMode} />
              <Newsletter mode={mode} toggleMode={toggleMode} />
            </Route>
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
