import React, { useEffect, useState } from 'react';
import './component.css';


function Top(props) {
    const [myStyle, setMyStyle] = useState({
        visibility: "hidden",
        opacity: 0,
    });
    // function for user profile visibiliy on click
    const toggleStyle = () => {
        if (myStyle.opacity === 0) {
            setMyStyle({
                visibility: "visible",
                opacity: 1,
            });
        }
        else {
            setMyStyle({
                visibility: "hidden",
                opacity: 0,
            });
        }
    }
    // onscrool nav padding change
    const [navStyle, setNavStyle] = useState("navbar navbar-expand-lg fixed-top py-0");
    useEffect(() => {
        window.addEventListener("scroll", () => {
            if (window.pageYOffset > 0) {
                setNavStyle("navbar navbar-expand-lg fixed-top py-0 px-1")
            } else {
                setNavStyle("navbar navbar-expand-lg fixed-top py-0")
            }
        });
    }, []);
    return (
        <nav className={navStyle}>
            <div className="container-fluid">
                <h2 className="logo">SARANG-Hey <img src="./images/sarang.png" /> </h2>
                <button className="navbar-toggler collapsed" type="button" data-bs-toggle="collapse"
                    data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span className="toggler-icon top-bar"></span>
                    <span className="toggler-icon middle-bar"></span>
                    <span className="toggler-icon bottom-bar"></span>
                </button>
                <div className="collapse navbar-collapse justify-content-end" id="navbarSupportedContent">
                    <ul className="navbar-nav ml-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <a className="nav-link active" aria-current="page" href="#"><i className="fas fa-home"></i> Home</a>
                        </li>
                        <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown"
                                aria-expanded="false">
                                <i className="fas fa-search"></i> Explore
                            </a>
                            <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                                <li><a className="dropdown-item" href="#suprise">Suprise Events</a></li>
                                <li><a className="dropdown-item" href="#special">Special Events</a></li>
                            </ul>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#"><i className="far fa-address-book"></i> About Us</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#footer"><i className="fas fa-phone-alt"></i> Contact Us</a>
                        </li>
                    </ul>
                    {/* <form className="d-flex">
                        <a className="btn outcolor" href="./login1/login.html">Sign In</a>
                    </form> */}
                    <div className="user">
                        <div className="user-profile" onClick={toggleStyle}>
                            <i className="fas fa-user" data-bs-toggle="tooltip" data-bs-placement="bottom"
                                title="User Profile"></i>
                        </div>
                        <div className="user-container" style={myStyle} >
                            <div className="user-name">
                                <h5>Nishant Rajput</h5>
                            </div>
                            <ul className="">
                                <li className=""><i className='far fa-user-circle'></i><a href="">My Profile</a></li>
                                <li className=""><i className="fas fa-pencil-alt"></i><a href="">Edit Profile</a></li>
                                <li className=""><i className="far fa-envelope"></i><a href="" >Inbox</a></li>
                                <li className=""><i className="fas fa-cog"></i><a href="">Setting</a></li>
                                <li className=""><i className="far fa-question-circle"></i><a href=""> Help</a></li>
                                <li className=""><i className="fas fa-sign-out-alt"></i><a href="">Logout</a></li>
                            </ul>
                        </div>
                    </div>
                    <div className={`form-check form-switch text-${props.mode }`}>
                        <input className="form-check-input" onClick={props.toggleMode} type="checkbox" role="switch" id="flexSwitchCheckDefault" />
                        <label className="form-check-label" htmlFor="flexSwitchCheckDefault">{props.mode === 'dark'?'Light':'Dark'}</label>
                    </div>
                </div>
            </div>
        </nav>
    );
}
export default Top;
